"use strict";

var selectSingle = document.querySelector(".select"),
  selectSingle_title = selectSingle.querySelector(".select__title"),
  selectSingle_labels = selectSingle.querySelectorAll(".select__label");
selectSingle_title.addEventListener("click", function () {
  "active" === selectSingle.getAttribute("data-state") ? selectSingle.setAttribute("data-state", "") : selectSingle.setAttribute("data-state", "active");
});
for (var e = 0; e < selectSingle_labels.length; e++) selectSingle_labels[e].addEventListener("click", function (e) {
  selectSingle_title.textContent = e.target.textContent, selectSingle.setAttribute("data-state", "");
});
var reset = document.querySelector(".reset");
reset.addEventListener("click", function () {
  selectSingle_title.textContent = selectSingle_title.getAttribute("data-default");
});